import React, { useState, useEffect } from 'react';
import { Layout, Menu, Avatar, Button } from 'antd';
import {
  UserOutlined,
  SettingOutlined,
  BookOutlined,
  HistoryOutlined
} from '@ant-design/icons';
import UserInfo from './components/UserInfo';
import PrivacySettings from './components/PrivacySettings';
import StudyPlan from './components/StudyPlan';
import KnowledgeTraining from './components/KnowledgeTraining';
import './index.less';

const { Header, Sider, Content } = Layout;

const UserCenter = () => {
  const [selectedMenu, setSelectedMenu] = useState('userInfo');
  const [userInfo, setUserInfo] = useState({
    avatar: '',
    nickname: '用户1234',
    focusTime: 0,
    lastLoginTime: '',
    phone: '',
    email: ''
  });

  // 渲染对应的内容组件
  const renderContent = () => {
    switch(selectedMenu) {
      case 'userInfo':
        return <UserInfo userInfo={userInfo} onUpdate={setUserInfo} />;
      case 'privacy':
        return <PrivacySettings />;
      case 'studyPlan':
        return <StudyPlan />;
      case 'knowledge':
        return <KnowledgeTraining />;
      default:
        return <UserInfo userInfo={userInfo} onUpdate={setUserInfo} />;
    }
  };

  return (
    <Layout className="user-center">
      <Header className="header">
        <div className="user-brief">
          <Avatar size={40} icon={<UserOutlined />} src={userInfo.avatar} />
          <span className="nickname">{userInfo.nickname}</span>
          <span className="focus-time">专注时长：{userInfo.focusTime}分钟</span>
        </div>
        <div className="quick-links">
          <Button type="link">首页</Button>
          <Button type="link">学习计划</Button>
          <Button type="link">反馈</Button>
          <Button type="primary">退出登录</Button>
        </div>
      </Header>
      
      <Layout>
        <Sider width={200}>
          <Menu
            mode="inline"
            selectedKeys={[selectedMenu]}
            onSelect={({key}) => setSelectedMenu(key)}
          >
            <Menu.Item key="userInfo" icon={<UserOutlined />}>
              个人信息
            </Menu.Item>
            <Menu.Item key="privacy" icon={<SettingOutlined />}>
              隐私设置
            </Menu.Item>
            <Menu.Item key="studyPlan" icon={<BookOutlined />}>
              学习计划
            </Menu.Item>
            <Menu.Item key="knowledge" icon={<HistoryOutlined />}>
              知识点训练
            </Menu.Item>
          </Menu>
        </Sider>
        
        <Content className="main-content">
          {renderContent()}
        </Content>
      </Layout>
    </Layout>
  );
};

export default UserCenter;
