import React, { useState } from 'react';
import { Layout, Menu, Button, Card, Input, Upload, Slider, InputNumber, Switch, Select, Table, Space, message } from 'antd';
import { UploadOutlined, SendOutlined, DownloadOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import './index.less';

const { Header, Content } = Layout;
const { TextArea } = Input;
const { Option } = Select;

const Home = () => {
  const [question, setQuestion] = useState('');
  const [difficulty, setDifficulty] = useState(5);
  const [exerciseCount, setExerciseCount] = useState(10);
  const [isAutoGenerate, setIsAutoGenerate] = useState(true);
  const [selectedSubject, setSelectedSubject] = useState('');
  const navigate = useNavigate();

  // 模拟的学习计划数据
  const studyPlanColumns = [
    {
      title: '时间段',
      dataIndex: 'timeSlot',
      key: 'timeSlot',
      width: 150,
    },
    {
      title: '学习内容',
      dataIndex: 'content',
      key: 'content',
    },
    {
      title: '难度系数',
      dataIndex: 'difficulty',
      key: 'difficulty',
      width: 100,
      render: (text) => <span style={{ color: '#1890ff' }}>{text}</span>,
    },
    {
      title: '习题',
      dataIndex: 'exercises',
      key: 'exercises',
      width: 100,
      render: () => (
        <Button type="link" icon={<DownloadOutlined />}>
          下载
        </Button>
      ),
    },
    {
      title: '完成情况',
      dataIndex: 'score',
      key: 'score',
      width: 120,
      render: (text) => text ? <span style={{ color: '#52c41a' }}>{text}</span> : '-',
    },
  ];

  const studyPlanData = [
    {
      key: '1',
      timeSlot: '08:00-09:30',
      content: '数学 - 函数与导数',
      difficulty: 7,
      exercises: 'math_1.pdf',
      score: '85/100',
    },
    {
      key: '2',
      timeSlot: '09:30-10:00',
      content: '休息 - 散步',
      difficulty: '-',
      exercises: '-',
      score: '-',
    },
    // ... 更多数据
  ];

  const handleSendQuestion = () => {
    if (!question.trim()) {
      message.warning('请输入问题');
      return;
    }
    // TODO: 发送问题到后端
    console.log('发送问题:', question);
  };

  const handleGeneratePlan = () => {
    // TODO: 生成学习计划
    message.success('学习计划生成成功！');
  };

  return (
    <Layout className="home-layout">
      <Header className="header">
        <div className="logo">学习助手</div>
        <Menu mode="horizontal" theme="dark">
          <Menu.Item key="plan">学习计划</Menu.Item>
          <Menu.Item key="feedback">反馈</Menu.Item>
          <Menu.Item key="user" onClick={() => navigate('/user')}>个人中心</Menu.Item>
        </Menu>
      </Header>

      <Content className="content">
        <Card title="AI 问答助手" className="qa-card">
          <div className="chat-container">
            {/* 这里可以添加聊天记录展示 */}
          </div>
          <div className="input-area">
            <TextArea
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="请输入您的问题..."
              autoSize={{ minRows: 2, maxRows: 6 }}
            />
            <Space className="action-buttons">
              <Upload>
                <Button icon={<UploadOutlined />}>上传图片</Button>
              </Upload>
              <Button 
                type="primary" 
                icon={<SendOutlined />}
                onClick={handleSendQuestion}
              >
                发送
              </Button>
            </Space>
          </div>
        </Card>

        <Card title="学习计划生成" className="plan-card">
          <div className="plan-controls">
            <div className="su">
              <Button 
                type="primary" 
                onClick={handleGeneratePlan}
              >
                生成学习计划
              </Button>
            </div>
            <Space direction="vertical" style={{ width: '100%' }}>
              <div className="control-item">
                <span className="label">自动生成：</span>
                <Switch
                  checked={isAutoGenerate}
                  onChange={setIsAutoGenerate}
                />
              </div>

              <div className="control-item">
                <span className="label">知识点：</span>
                <Select
                  style={{ width: 200 }}
                  placeholder="选择知识点"
                  value={selectedSubject}
                  onChange={setSelectedSubject}
                  disabled={isAutoGenerate}
                >
                  <Option value="math">数学</Option>
                  <Option value="physics">物理</Option>
                  <Option value="programming">编程</Option>
                </Select>
              </div>

              <div className="control-item">
                <span className="label">难度系数：</span>
                <Slider
                  min={1}
                  max={10}
                  value={difficulty}
                  onChange={setDifficulty}
                  disabled={isAutoGenerate}
                  style={{ width: 200 }}
                />
              </div>

              <div className="control-item">
                <span className="label">习题数量：</span>
                <InputNumber
                  min={1}
                  max={50}
                  value={exerciseCount}
                  onChange={setExerciseCount}
                  disabled={isAutoGenerate}
                />
              </div>
            </Space>
          </div>

          <Table
            columns={studyPlanColumns}
            dataSource={studyPlanData}
            className="plan-table"
            pagination={false}
          />
        </Card>
      </Content>
    </Layout>
  );
};

export default Home;
