import React from 'react';
import { Card, Progress, List, Tag } from 'antd';

const KnowledgeTraining = () => {
  const knowledgePoints = [
    {
      key: '1',
      topic: '函数与导数',
      mastery: 8,
      records: [
        { date: '2024-03-19', score: 90, problems: 10 },
        { date: '2024-03-20', score: 85, problems: 8 }
      ]
    },
    {
      key: '2',
      topic: '概率统计',
      mastery: 6,
      records: [
        { date: '2024-03-18', score: 75, problems: 12 }
      ]
    }
  ];

  return (
    <div className="knowledge-training">
      <h2>知识点训练记录</h2>
      <List
        dataSource={knowledgePoints}
        renderItem={item => (
          <Card 
            title={item.topic}
            style={{ marginBottom: 16 }}
            extra={
              <Tag color="blue">掌握度: {item.mastery}/10</Tag>
            }
          >
            <Progress percent={item.mastery * 10} />
            <List
              size="small"
              header={<div>练习记录</div>}
              dataSource={item.records}
              renderItem={record => (
                <List.Item>
                  <span>{record.date}</span>
                  <span>得分：{record.score}</span>
                  <span>题目数：{record.problems}</span>
                </List.Item>
              )}
            />
          </Card>
        )}
      />
    </div>
  );
};

export default KnowledgeTraining;
