import React from 'react';
import { Card, Table, Progress } from 'antd';

const StudyPlan = () => {
  const planData = [
    {
      key: '1',
      date: '2024-03-20',
      subject: '数学',
      content: '函数与导数',
      status: '已完成',
      completion: 100,
    },
    {
      key: '2',
      date: '2024-03-21',
      subject: '物理',
      content: '力学基础',
      status: '进行中',
      completion: 60,
    }
  ];

  const columns = [
    {
      title: '日期',
      dataIndex: 'date',
      key: 'date',
    },
    {
      title: '科目',
      dataIndex: 'subject',
      key: 'subject',
    },
    {
      title: '学习内容',
      dataIndex: 'content',
      key: 'content',
    },
    {
      title: '状态',
      dataIndex: 'status',
      key: 'status',
    },
    {
      title: '完成度',
      dataIndex: 'completion',
      key: 'completion',
      render: (completion) => (
        <Progress percent={completion} size="small" />
      ),
    },
  ];

  return (
    <div className="study-plan">
      <h2>学习计划</h2>
      <Card title="当前学习计划" style={{ marginBottom: 24 }}>
        <Table columns={columns} dataSource={planData} />
      </Card>
    </div>
  );
};

export default StudyPlan;
