import React, { useState } from 'react';
import { Layout, Menu, Button, Card, Input, Upload, Space, message } from 'antd';
import { UploadOutlined, SendOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import './index.less';

const { Header, Content } = Layout;
const { TextArea } = Input;

const Home = () => {
  const [question, setQuestion] = useState('');
  const navigate = useNavigate();

  const handleSendQuestion = () => {
    if (!question.trim()) {
      message.warning('请输入问题');
      return;
    }
    // TODO: 发送问题到后端
    console.log('发送问题:', question);
  };

  return (
    <Layout className="home-layout">
      <Header className="header">
        <div className="logo">学习助手</div>
        <Menu mode="horizontal" theme="dark">
          <Menu.Item key="plan" onClick={() => navigate('/study-plan')}>学习计划</Menu.Item>
          <Menu.Item key="feedback" onClick={() => navigate('/feedback')}>反馈</Menu.Item>
          <Menu.Item key="user" onClick={() => navigate('/user')}>个人中心</Menu.Item>
        </Menu>
      </Header>

      <Content className="content">
        <Card title="AI 问答助手" className="qa-card">
          <div className="chat-container">
            {/* 这里可以添加聊天记录展示 */}
          </div>
          <div className="input-area">
            <TextArea
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="请输入您的问题..."
              autoSize={{ minRows: 2, maxRows: 6 }}
            />
            <Space className="action-buttons">
              <Upload>
                <Button icon={<UploadOutlined />}>上传图片</Button>
              </Upload>
              <Button 
                type="primary" 
                icon={<SendOutlined />}
                onClick={handleSendQuestion}
              >
                发送
              </Button>
            </Space>
          </div>
        </Card>
      </Content>
    </Layout>
  );
};

export default Home;
