import React, { useState } from 'react';
import { Layout, Menu, Button, Card, Input, Upload, Space, message, TimePicker, Form, Typography } from 'antd';
import { UploadOutlined, SendOutlined, RobotOutlined, ScheduleOutlined } from '@ant-design/icons';
import { useNavigate } from 'react-router-dom';
import moment from 'moment';
import './index.less';

const { Header, Content } = Layout;
const { TextArea } = Input;
const { Title, Paragraph } = Typography;

const Home = () => {
  const [question, setQuestion] = useState('');
  const [form] = Form.useForm();
  const navigate = useNavigate();

  const handleSendQuestion = () => {
    if (!question.trim()) {
      message.warning('请输入问题');
      return;
    }
    // TODO: 发送问题到后端
    message.success('问题已发送，正在为您解答...');
    console.log('发送问题:', question);
  };

  const handleGeneratePlan = async () => {
    try {
      const values = await form.validateFields();
      const timeRange = {
        start: values.timeRange[0].format('HH:mm'),
        end: values.timeRange[1].format('HH:mm')
      };
      
      // TODO: 发送到后端生成计划
      console.log('空闲时间段：', timeRange);
      message.success('学习计划生成成功！即将跳转到学习计划页面...');
      setTimeout(() => navigate('/study-plan'), 1500);
    } catch (error) {
      message.error('请填写完整的时间段信息');
    }
  };

  return (
    <Layout className="home-layout">
      <Header className="header">
        <div className="logo">学习助手</div>
        <Menu mode="horizontal" theme="dark">
          <Menu.Item key="home" onClick={() => navigate('/')}>首页</Menu.Item>
          <Menu.Item key="plan" onClick={() => navigate('/study-plan')}>学习计划</Menu.Item>
          <Menu.Item key="feedback" onClick={() => navigate('/feedback')}>反馈</Menu.Item>
          <Menu.Item key="user" onClick={() => navigate('/user')}>个人中心</Menu.Item>
        </Menu>
      </Header>

      <Content className="content">
        <div className="welcome-section">
          <Title level={2}>欢迎使用学习助手</Title>
          <Paragraph>
            这是一个智能学习辅助系统，可以帮助您规划学习时间，解答学习疑惑，提高学习效率。
          </Paragraph>
        </div>

        <Card 
          title={
            <Space>
              <RobotOutlined style={{ fontSize: '20px', color: '#1890ff' }} />
              <span>AI 问答助手</span>
            </Space>
          } 
          className="qa-card"
          bordered={false}
        >
          <div className="chat-container">
            {/* 这里可以添加聊天记录展示 */}
            <div className="chat-placeholder">
              <RobotOutlined style={{ fontSize: '48px', color: '#1890ff' }} />
              <p>有任何学习上的问题都可以问我哦！</p>
            </div>
          </div>
          <div className="input-area">
            <TextArea
              value={question}
              onChange={(e) => setQuestion(e.target.value)}
              placeholder="请输入您的问题..."
              autoSize={{ minRows: 2, maxRows: 6 }}
            />
            <Space className="action-buttons">
              <Upload>
                <Button icon={<UploadOutlined />}>上传图片</Button>
              </Upload>
              <Button 
                type="primary" 
                icon={<SendOutlined />}
                onClick={handleSendQuestion}
              >
                发送
              </Button>
            </Space>
          </div>
        </Card>

        <Card 
          title={
            <Space>
              <ScheduleOutlined style={{ fontSize: '20px', color: '#1890ff' }} />
              <span>学习计划生成</span>
            </Space>
          } 
          className="plan-card"
          bordered={false}
        >
          <Form
            form={form}
            initialValues={{
              timeRange: [moment('07:00', 'HH:mm'), moment('21:00', 'HH:mm')]
            }}
          >
            <Form.Item
              name="timeRange"
              label="空闲时间段"
              rules={[{ required: true, message: '请选择时间段' }]}
            >
              <TimePicker.RangePicker 
                format="HH:mm"
                placeholder={['开始时间', '结束时间']}
                style={{ width: '300px' }}
              />
            </Form.Item>
            <div className="button-container">
              <Button 
                type="primary" 
                onClick={handleGeneratePlan}
                icon={<ScheduleOutlined />}
                size="large"
              >
                生成学习计划
              </Button>
            </div>
          </Form>
        </Card>
      </Content>
    </Layout>
  );
};

export default Home;
